import React, { useEffect, useState } from 'react';

const HomePage = () => {
  const [filmes, setFilmes] = useState([]);
  const [sessoes, setSessoes] = useState([]);

  useEffect(() => {
    const dadosFilmes = localStorage.getItem('filmes');
    const dadosSessoes = localStorage.getItem('sessoes');

    if (dadosFilmes) {
      setFilmes(JSON.parse(dadosFilmes));
    }

    if (dadosSessoes) {
      setSessoes(JSON.parse(dadosSessoes));
    }
  }, []);

  // üî• Filtrar os filmes que possuem pelo menos uma sess√£o
  const filmesComSessoes = filmes.filter((filme) =>
    sessoes.some((sessao) => sessao.filme === filme.nome)
  );

  // üß† Fun√ß√£o para buscar as sess√µes de um filme espec√≠fico
  const buscarSessoesDoFilme = (filmeNome) => {
    return sessoes.filter((sessao) => sessao.filme === filmeNome);
  };

  return (
    <div className="container mt-4">
      <h2>üé¨ Filmes em Cartaz</h2>
      <div className="row">
        {filmesComSessoes.length === 0 && (
          <p>Nenhum filme dispon√≠vel em sess√µes cadastradas.</p>
        )}

        {filmesComSessoes.map((filme, index) => (
          <div className="col-md-4 mb-4" key={index}>
            <div className="card h-100">
              {filme.imagem ? (
                <img
                  src={filme.imagem}
                  className="card-img-top"
                  alt={filme.nome}
                  style={{ height: '550px', objectFit: 'cover' }}
                />
              ) : (
                <div
                  className="d-flex align-items-center justify-content-center bg-secondary text-white"
                  style={{ height: '350px' }}
                >
                  Sem Imagem
                </div>
              )}
              <div className="card-body">
                <h5 className="card-title">{filme.nome}</h5>
                <p className="card-text">
                  <strong>G√™nero:</strong> {filme.genero}
                  <br />
                  <strong>Dura√ß√£o:</strong> {filme.duracao}
                </p>

                <h6>Sess√µes Dispon√≠veis:</h6>
                {buscarSessoesDoFilme(filme.nome).length === 0 ? (
                  <p className="text-muted">Nenhuma sess√£o dispon√≠vel.</p>
                ) : (
                  <ul className="list-group list-group-flush">
                    {buscarSessoesDoFilme(filme.nome).map((sessao, idx) => (
                      <li className="list-group-item" key={idx}>
                        <strong>Sala:</strong> {sessao.sala} <br />
                        <strong>Hor√°rio:</strong>{' '}
                        {sessao.horario.replace('T', ' ')}
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
