import React, { useEffect, useState } from 'react';
import TextInput from '../../components/inputs/Input';
import Button from '../../components/buttons/Button';

const FilmePage = () => {
  const [filmes, setFilmes] = useState([]);
  const [nome, setNome] = useState('');
  const [genero, setGenero] = useState('');
  const [duracao, setDuracao] = useState('');
  const [imagem, setImagem] = useState('');
  const [editIndex, setEditIndex] = useState(null);

  // Carregar dados do localStorage
  useEffect(() => {
    const dados = JSON.parse(localStorage.getItem('filmes')) || [];
    setFilmes(dados);
  }, []);

  // Salvar no localStorage sempre que filmes mudar
  useEffect(() => {
    localStorage.setItem('filmes', JSON.stringify(filmes));
  }, [filmes]);

  const limparFormulario = () => {
    setNome('');
    setGenero('');
    setDuracao('');
    setImagem('');
    setEditIndex(null);
  };

  const salvarFilme = (e) => {
    e.preventDefault();

    const filme = { nome, genero, duracao, imagem };

    if (editIndex !== null) {
      const novosFilmes = [...filmes];
      novosFilmes[editIndex] = filme;
      setFilmes(novosFilmes);
    } else {
      setFilmes([...filmes, filme]);
    }

    limparFormulario();
  };

  const editarFilme = (index) => {
    const filme = filmes[index];
    setNome(filme.nome);
    setGenero(filme.genero);
    setDuracao(filme.duracao);
    setImagem(filme.imagem);
    setEditIndex(index);
  };

  const excluirFilme = (index) => {
    if (window.confirm('Deseja realmente excluir esse filme?')) {
      const novosFilmes = filmes.filter((_, i) => i !== index);
      setFilmes(novosFilmes);
    }
  };

  return (
    <div className="container mt-4">
      <h2>🎥 Gerenciar Filmes</h2>

      <form onSubmit={salvarFilme}>
        <TextInput
          label="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          placeholder="Digite o nome do filme"
        />
        <TextInput
          label="Gênero"
          value={genero}
          onChange={(e) => setGenero(e.target.value)}
          placeholder="Digite o gênero"
        />
        <TextInput
          label="Duração"
          value={duracao}
          onChange={(e) => setDuracao(e.target.value)}
          placeholder="Ex: 120 min"
        />
        <TextInput
          label="URL da Imagem"
          value={imagem}
          onChange={(e) => setImagem(e.target.value)}
          placeholder="Cole a URL da imagem do filme"
        />

        <Button
          label={editIndex !== null ? 'Atualizar Filme' : 'Adicionar Filme'}
        />
      </form>

      <hr />

      <h4>Lista de Filmes</h4>
      {filmes.length === 0 && <p>Nenhum filme cadastrado.</p>}

      {filmes.length > 0 && (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Imagem</th>
              <th>Nome</th>
              <th>Gênero</th>
              <th>Duração</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {filmes.map((filme, index) => (
              <tr key={index}>
                <td>
                  {filme.imagem ? (
                    <img
                      src={filme.imagem}
                      alt={filme.nome}
                      width="80"
                      height="120"
                      style={{ objectFit: 'cover' }}
                    />
                  ) : (
                    'Sem imagem'
                  )}
                </td>
                <td>{filme.nome}</td>
                <td>{filme.genero}</td>
                <td>{filme.duracao}</td>
                <td>
                  <button
                    className="btn btn-sm btn-warning me-2"
                    onClick={() => editarFilme(index)}
                  >
                    <i className="bi bi-pencil"></i>
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => excluirFilme(index)}
                  >
                    <i className="bi bi-trash"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default FilmePage;
