import React, { useEffect, useState } from 'react';

const HomePage = () => {
  const [filmes, setFilmes] = useState([]);

  const carregarFilmes = () => {
    const dados = localStorage.getItem('filmes');
    return dados ? JSON.parse(dados) : [];
  };

  useEffect(() => {
    const dados = carregarFilmes();
    setFilmes(dados);
  }, []);

  return (
    <div className="container mt-4">
      <h2>ðŸŽ¬ Filmes em Cartaz</h2>
      <div className="row">
        {filmes.length === 0 && <p>Nenhum filme cadastrado.</p>}

        {filmes.map((filme, index) => (
          <div className="col-md-3 mb-4" key={index}>
            <div className="card h-100">
              {filme.imagem ? (
                <img
                  src={filme.imagem}
                  className="card-img-top"
                  alt={filme.nome}
                  style={{ height: '350px', objectFit: 'cover' }}
                />
              ) : (
                <div
                  className="d-flex align-items-center justify-content-center bg-secondary text-white"
                  style={{ height: '350px' }}
                >
                  Sem Imagem
                </div>
              )}
              <div className="card-body">
                <h5 className="card-title">{filme.nome}</h5>
                <p className="card-text">
                  <strong>GÃªnero:</strong> {filme.genero}<br />
                  <strong>DuraÃ§Ã£o:</strong> {filme.duracao}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
